/*
This component handles all the Login page functionalities 
*/

import Backdrop from "@mui/material/Backdrop";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import CircularProgress from "@mui/material/CircularProgress";
import Grid from "@mui/material/Grid";
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import {useState} from "react";
import { Navigate } from "react-router-dom";
import {Link} from "react-router-dom";

const {succLogin} = false;
const initData = {
	username: {
		value: "",
		error: false,
		errorMessage: "Please enter valid username!",
	},
	password: {
		value: "",
		error: false,
		errorMessage: "Please enter valid password!",
	},
};
const [formInputs, setFormInputs] = useState(initData);
const Login = () => {

	let validate = () => {
		let data = {
			...formInputs
		};
		let requestJson = {};
		let validInputs = true;
		for(let k in formInputs) {
			let json = getValidity(k, formInputs[k].value);
			data[k] = {
				value: data[k].value,
				error: !json.valid,
				errorMessage: json.message,
			};
			validInputs = validInputs && json.valid;
			if(json.valid) {
				requestJson[k] = data[k].value;
			}
		}
		setFormInputs(data);
		if(validInputs) {
			login(requestJson.username, requestJson.password).then(() => {
				broadcastMessage("Logged in Successfully!", "success");
			}).catch(json => {
				broadcastMessage(json.reason, "error");
			});
		} 
	};

	let regexMatcher = (exp,value) => {
		let regex = new RegExp(exp);
		return regex.test(value);
	}

	let getValidity = (field, value) => {
		let valid = true;
		let errorMsg = null;
		if(value == null || value.length === 0) {
			valid = false;
			errorMsg = "This field is required.";
		} else {
			switch (field) {
				case "username": {
					if(value.length > 255) {
						valid = false;
						errorMsg = "Email cant be more than 255 characters long";
					} else {
						valid = regexMatcher(value, "^\\w+([\\.-]?\\w+)*@\\w+([\\.-]?\\w+)*(\\.\\w{2,3})+$");
						errorMsg = "Please enter valid email!";
					}
					break;
				}
				case "password": {
					if (value.length < 6 || 40 < value.length) {
						valid = false;
						message = "Password's length must be between 6 and 40."
					}
					break;
				}
				default : {
					return;
				}
			}
		}
		return {
			valid,
			message
		};
	};

	let validateAndSaveLoginData = (fieldName, fieldValue) => {
		let json = getValidity(fieldName, fieldValue);
		let data = {
			...formInputs
		};
		data[fieldName] = {
			value: data[fieldName].value,
			error: !json.valid,
			errorMessage: json.message,
		}
		setFormInputs(data);
	};

	let saveOnFieldChange = (field, value) => {
		setFormInputs({
			...formInputs,
			[field]:{
				...formInputs[field],
				value
			}
		});
	};

	if(succLogin === false) {
		return (
			<Box sx={{flexGrow: 1}}>
				<Grid container spacing={1}>
					<Grid container item spacing={3}>
						<Grid item xs={4}/>
						<Grid item xs={4}>
							<div style={{display: 'flex', justifyContent: 'center', marginTop: "10%"}}>
								<LockOutlinedIcon style={{
									display: 'inline-block',
									borderRadius: '60px',
									padding: '0.6em 0.6em',
									color: '#ffffff',
									background: "#f50057"
								}}/>
							</div>
							<div style={{display: 'flex', justifyContent: 'center'}}>
								<Typography
									variant="subtitle1"
									noWrap
									sx={{
										fontSize: "25px",
										color: 'inherit',
									}}
								>
									Sign in
								</Typography>
							</div>
							<div style={{display: 'flex', justifyContent: 'center', marginTop: "30px"}}>
								<TextField id="username"
										   label="Email Address *"
										   variant="outlined"
										   fullWidth
										   type="email"
										   value={formInputs.username.value}
										   onChange={(event) => saveOnFieldChange("username", event.target.value)}
										   onBlur={(event) => validateAndSaveLoginData("username", event.target.value)}
										   error={formInputs.username.error}
										   helperText={formInputs.username.error && formInputs.username.errorMessage}
								/>
							</div>
							<div style={{display: 'flex', justifyContent: 'center', marginTop: "30px"}}>
								<TextField id="password"
										   label="Password *"
										   variant="outlined"
										   fullWidth
										   type="password"
										   value={formInputs.password.value}
										   onChange={(event) => saveOnFieldChange("password", event.target.value)}
										   onBlur={(event) => validateAndSaveLoginData("password", event.target.value)}
										   error={formInputs.password.error}
										   helperText={formInputs.password.error && formInputs.password.errorMessage}
								/>
							</div>
							<div style={{display: 'flex', justifyContent: 'center', marginTop: "30px"}}>
								<Button variant="contained"
										color="primary"
										fullWidth
										onClick={validate}
								>
									SIGN IN
								</Button>
							</div>
							<div style={{display: 'flex', justifyContent: 'left', marginTop: "30px"}}>
								<Link to="/signup">
									<Typography variant="body1">
										Don't have an account? Sign Up
									</Typography>
								</Link>
							</div>
						</Grid>
						<Grid item xs={4}/>
					</Grid>
				</Grid>
				<Backdrop
					sx={{color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1}}
					open={busy}
				>
					<CircularProgress color="inherit"/>
				</Backdrop>
			</Box>
		);
	} else {
		return (
			<Navigate to="/home"/>
		);
	}
};

export default Login;
