import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import {ShoppingCart} from "@mui/icons-material";
import {Link} from "react-router-dom";


const NavigationBar = () => {

	const succLogin = true;
	const components = [
		{
			id: "1",
			labelTxt: "Home",
			path: "/home",
			displayCondition : succLogin == null,
		},
		{
			id: "2",
			labelTxt: "Login",
			path: "/login",
			displayCondition: succLogin == null,
		},
		{
			id: "3",
			labelTxt: "Sign Up",
			path: "/signup",
			displayCondition: succLogin == null,
		},
	];


	return (
		<AppBar sx={{ bgcolor: "#3f51b5", position: 'fixed' }}>
			<Container maxWidth={false}>
				<Toolbar>
					<ShoppingCart />
					<Typography
						variant="h6"
						noWrap
						href="/home"
					>
						upGrad E-Shop
					</Typography>

					<Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
						<IconButton
							size="large"
							aria-label="account of current user"
							aria-controls="menu-appbar"
							aria-haspopup="true"
							color="inherit"
						>
							<MenuIcon />
						</IconButton>
						<Menu
							id="menu-appbar"
							anchorOrigin={{
								vertical: 'bottom',
								horizontal: 'left',
							}}
							keepMounted
							transformOrigin={{
								vertical: 'top',
								horizontal: 'left',
							}}
							sx={{
								display: { xs: 'block', md: 'none' },
							}}
						>
							{components.map((element) => {
								if (element.displayCondition) {
									return (
										<MenuItem key={"menu_item_" + element.id}>
											<Link key={"link_" + element.id} to={element.path} style={{textDecoration: "none"}}>
												<Typography textAlign="center">{element.labelTxt}</Typography>
											</Link>
										</MenuItem>
									);
								}
								return null;
							})}
							{
								succLogin != null 
								//Need to add logout here
							}
						</Menu>
					</Box>


					<Box sx={{ flexGrow: 1 }} />
					<Box sx={{ display: { xs: 'none', md: 'flex' } }}>
						{components.map((element) => {
							if (element.displayCondition) {
								return (
									<Link key={"main_link_" + element.id} to={element.path} style={{ textDecoration: "none" }}>
										<Button
											key={"button_link_" + element.id}
											sx={{ my: 2, color: 'white', display: 'block', textTransform: "none" }}
										>
											<u>{element.labelTxt}</u>
										</Button>
									</Link>
								);
							}
							return null;
						})}
						{
							succLogin != null 
							//Need to add logout here
						}
					</Box>
				</Toolbar>
			</Container>
		</AppBar>
	);
};

export default NavigationBar;
